(function() {
	tinymce.PluginManager.add('ImageUpload', function(editor, url){
		editor.addButton('ImageUpload',{
			image : url + '/photo_icon.png',
			title : '相片上傳',
			onclick : function(){
				editor.windowManager.open({
					title : '相片上傳',
					width : 400,
					height : 70,
					inline : true,
					file : url + '/dialog.html?',
					buttons: [{
	                    text: '關閉',
	                    onclick: 'close'
	                }]
				});
			}
		});
	});
})();
